# function literals
var f1 := [x] -> x^(3) - 2x^(2) + x - 1;
print(f1, f1(3));

# derive functions
var fder := derive(f1);
print(fder, fder(3));

# integrate functions
var fint := integrate(f1);
print(fint, fint(3));

# matrices
var mat1 := mat<3, 2>(1, 0,
                      0, 1,
                      2, 4);
var mat2 := mat<2, 2>(3, -2,
                      5,  1);
print(mat1);
print(mat1 * mat2);

# basic math functions
print(fact(5)); # 5! = 120
print(binomial(10, 8)); # 10 choose 8
print(cos(90)); # cos(90) = 0 using degrees
